// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;




contract Counter {
  uint256 public counter;

  event Increment(uint currentValue, address indexed sender);
  event Decrement(uint currentValue, address indexed sender);

  constructor(){
    counter = 0;
  }

  function increment() public {
    counter++;
    emit Increment(counter, msg.sender);
  }

  function decrement() public {
      counter--;
      emit Decrement(counter, msg.sender);
  }



}
